import React from "react";
import { useState } from "react";

const AddMovie = ({ onAdd }) => {
  const [movieName, setMovieName] = useState("");
  const [moviePriority, setMoviePriority] = useState(false);
  const onSubmit = e => {
    e.preventDefault();

    if (!movieName) {
      alert("Write down the movie name");
    }
  };
  return (
    <div className="bg-light m-2 rounded p-5">
      <form className="p-2" onSubmit={onSubmit}>
        <div className="form-group">
          <input
            type="text"
            className="form-control"
            placeholder="Enter movie name"
            value={movieName}
            onChange={event => setMovieName(event.target.value)}
          ></input>
        </div>
        <div className="form-check">
          <input
            type="checkbox"
            className="form-check-input"
            value={moviePriority}
            onChange={event => setMoviePriority(event.currentTarget.checked)}
          />
          <label className="form-check-label">Priority</label>
        </div>
        <input
          type="submit"
          className=" mt-2 btn btn-block btn-dark"
          value="Add Movie"
        />
      </form>
    </div>
  );
};

export default AddMovie;
